mapper.select:
WITH SUBJ AS --확정된 과제정보 ( SELECT X.CO_CD AS CO_CD --회사코드 , X.SALES_CD AS SALES_CD --SALES Code , X.SJ_NM AS SJ_NM --과제명 , X.VER_NO AS VER_NO --버젼 , (SELECT FN_CM06M01_ID_TO_NM(X.SMRIZE_ID) FROM DUAL) AS SMRIZE_NM --총괄PM명 , TO_CHAR(X.BEGIN_DT, 'YYYY-MM-DD') AS BEGIN_DT --시작일 , TO_CHAR(X.DE_DT, 'YYYY-MM-DD') AS DE_DT --출고일 , TO_CHAR(X.ACPTNC_DT, 'YYYY-MM-DD') AS ACPTNC_DT --완료검수일 , TO_CHAR(X.REQRE_DAYCNT) AS REQRE_DAYCNT --소요일수 , X.DSGN_PLAN_HOUR AS DSGN_PLAN_HOUR --설계계획공수 , X.PRDCTN_PLAN_HOUR AS PRDCTN_PLAN_HOUR --생산계획공수 , X.FILE_TRGT_KEY FROM TB_WB21M01 X WHERE X.CO_CD = #{coCd} AND X.BEGIN_DT BETWEEN #{beginDt} AND #{deDt} --조회조건 : 과제시작일 AND X.CLOSE_YN = 'Y'
AND X.SALES_CD = #{salesCd}
) , SJD AS --수주상세 ( SELECT M.CO_CD AS CO_CD --회사코드 , (SELECT FN_CM05M01_CD_TO_NM(M.CO_CD) FROM DUAL) AS CO_NM --회사명 , M.ORDRS_NO AS ORDRS_NO --수주번호 , X.SALES_CD AS SALES_CD --SALES Code , M.ORDRS_CLNT_CD AS CLNT_CD --고객코드 , C.CLNT_NM AS CLNT_NM --고객명 , M.CLNT_PJT AS CLNT_PJT --고객사프로젝트 , (SELECT FN_CM05M01_CD_TO_NM(M.CLNT_PJT) FROM DUAL) AS CLNT_PJT_NM --고객사프로젝트명 , X.ORDRS_PRC_MACH AS ORDRS_PRC_MACH --수주가-기계파트 , X.TRGT_PCHS_PCOST_MACH AS TRGT_PCOST_MACH--목표구매원가-기계파트 FROM TB_CR02M01 M --수주마스터 INNER JOIN TB_CR02D02 AS X ON X.CO_CD = M.CO_CD AND X.ORDRS_NO = M.ORDRS_NO--수주상세 INNER JOIN TB_BM02M01 AS C ON M.ORDRS_CLNT_CD = C.CLNT_CD --거래처정보 WHERE 1=1 AND M.CO_CD = #{coCd} AND X.SALES_CD IS NOT NULL --비용제외 AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = X.CO_CD AND P.SALES_CD = X.SALES_CD ) ) , EVAL AS --WBS 평가정보 ( SELECT W.CO_CD AS CO_CD --회사코드 , W.SALES_CD AS SALES_CD --SALSES_CD , TO_CHAR(W.EVL_DT, 'YYYY-MM-DD') AS EVL_DT --평가일자 , W.EVL_ID AS EVL_ID --평가자ID , (SELECT FN_CM06M01_ID_TO_NM(W.EVL_ID) FROM DUAL) AS EVL_NM --평가자명 , W.CLOSE_YN AS CLOSE_YN --확정여부 , W.EVL_CNTS AS EVL_CNTS --평가내용 , W.FILE_TRGT_KEY AS FILE_TRGT_KEY FROM TB_WB25M01 W--WBS평가 WHERE W.CO_CD = #{coCd} AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = W.CO_CD AND P.SALES_CD = W.SALES_CD ) ) , RSLT AS --WBS 실적정보 ( SELECT WR.CO_CD AS CO_CD--회사코드 , WR.SALES_CD AS SALES_CD--SALES Code , MIN(WR.WBS_RSLTSS_DT) AS RSLTSS_DT --TASK실적시작일 , MAX(WR.WBS_RSLTSE_DT) AS RSLTSE_DT --TASK실적종료일 , SUM(WR.DAYCNT) AS RSLT_DAYCNT --TASK실적소요일 , SUM(WR.WBS_RSLTS_MH) AS RSLTS_MH --WBS실적 공수 , MAX(CASE WHEN WR.WBS_PLAN_CODE_KIND = 'WBSCODE15' AND WR.WBS_RSLTS_RATE = 100 THEN 'Y' ELSE 'N' END) AS WBS_CLOSE_YN --평가대상 유무 : 완료검수 실적(WBSCODE15) = 100% FROM TB_WB23M01 WR--WBS실적 정보 WHERE WR.CO_CD = #{coCd} AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = WR.CO_CD AND P.SALES_CD = WR.SALES_CD ) GROUP BY WR.CO_CD, WR.SALES_CD ) , ISU AS --WBS 이슈정보 ( SELECT ISU.CO_CD --회사코드 , ISU.SALES_CD --SALES Code , SUM(CASE WHEN ISU.WBS_PLAN_NO IS NOT NULL THEN 1 ELSE 0 END ) AS PLAN_CNT --계획 이슈 , SUM(CASE WHEN ISU.WBS_RSLTS_NO IS NOT NULL THEN 1 ELSE 0 END ) AS RSLT_CNT --실적 이슈 FROM TB_WB24M02 AS ISU --WBS이슈 정보 WHERE CO_CD = #{coCd} AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = ISU.CO_CD AND P.SALES_CD = ISU.SALES_CD ) GROUP BY CO_CD, SALES_CD ) SELECT COUNT(*) FROM ( SELECT X.CO_CD AS CO_CD --회사코드 , CR.ORDRS_NO AS ORDRS_NO --수주번호 , CR.CLNT_CD AS CLNT_CD --고객코드 , CR.CLNT_NM AS CLNT_NM --고객명 , CR.CLNT_PJT AS CLNT_PJT --고객사프로젝트 , CR.CLNT_PJT_NM AS CLNT_PJT_NM --고객사프로젝트명 , X.SJ_NM AS SJ_NM --과제명 , X.VER_NO AS VER_NO --과제버젼 , X.SALES_CD AS SALES_CD --SALES Code , X.SMRIZE_NM AS SMRIZE_NM --총괄PM명 , X.BEGIN_DT AS BEGIN_DT --과제시작일 , X.DE_DT AS DE_DT --출고일 , X.ACPTNC_DT AS ACPTNC_DT --완료검수일 , X.REQRE_DAYCNT AS REQRE_DAYCNT --소요일수 , RS.RSLTSS_DT AS RSLTSS_DT --TASK실적시작일 , RS.RSLTSE_DT AS RSLTSE_DT --TASK실적종료일 , RS.RSLT_DAYCNT AS RSLT_DAYCNT --TASK실적소요일 , RS.RSLTS_MH AS RSLTS_MH --TASK실적 공수 , ISU.PLAN_CNT AS PLAN_CNT --계획 이슈 , ISU.RSLT_CNT AS RSLT_CNT --실적 이슈 , NVL(RS.WBS_CLOSE_YN,'N') AS WBS_CLOSE_YN --평가대상 , EV.EVL_DT AS EVL_DT --평가일자 , EV.EVL_NM AS EVL_NM --평가자명 , EV.CLOSE_YN AS PLAN_CLOSE_YN --계획확정여부 , EV.EVL_CNTS AS EVL_CNTS --평가내용 , EV.FILE_TRGT_KEY AS FILE_TRGT_KEY --FILE_TRGT_KEY FROM SUBJ X INNER JOIN SJD AS CR ON X.CO_CD = CR.CO_CD AND X.SALES_CD = CR.SALES_CD LEFT OUTER JOIN EVAL AS EV ON X.CO_CD = EV.CO_CD AND X.SALES_CD = EV.SALES_CD LEFT OUTER JOIN RSLT AS RS ON X.CO_CD = RS.CO_CD AND X.SALES_CD = RS.SALES_CD LEFT OUTER JOIN ISU AS ISU ON X.CO_CD = ISU.CO_CD AND X.SALES_CD = ISU.SALES_CD WHERE 1=1 ) T WHERE 1=1 AND T.CO_CD = #{coCd}
AND T.SALES_CD = #{salesCd}
AND T.CLOSE_YN = #{closeYn}
AND T.WBS_CLOSE_YN = #{wbsCloseYn}
AND T.CLNT_PJT = #{clntPjt}
AND T.CLNT_NM LIKE '%' || #{ordrsClntNm} || '%'
AND T.BEGIN_DT BETWEEN #{beginDt} AND #{deDt}

WITH SUBJ AS --확정된 과제정보 ( SELECT X.CO_CD AS CO_CD --회사코드 , X.SALES_CD AS SALES_CD --SALES Code , X.SJ_NM AS SJ_NM --과제명 , X.VER_NO AS VER_NO --버젼 , (SELECT FN_CM06M01_ID_TO_NM(X.SMRIZE_ID) FROM DUAL) AS SMRIZE_NM --총괄PM명 , TO_CHAR(X.BEGIN_DT, 'YYYY-MM-DD') AS BEGIN_DT --시작일 , TO_CHAR(X.DE_DT, 'YYYY-MM-DD') AS DE_DT --출고일 , TO_CHAR(X.ACPTNC_DT, 'YYYY-MM-DD') AS ACPTNC_DT --완료검수일 , TO_CHAR(X.REQRE_DAYCNT) AS REQRE_DAYCNT --소요일수 , X.DSGN_PLAN_HOUR AS DSGN_PLAN_HOUR --설계계획공수 , X.PRDCTN_PLAN_HOUR AS PRDCTN_PLAN_HOUR --생산계획공수 , X.FILE_TRGT_KEY FROM TB_WB21M01 X WHERE X.CO_CD = #{coCd} AND X.BEGIN_DT BETWEEN #{beginDt} AND #{deDt} --조회조건 : 과제시작일 AND X.CLOSE_YN = 'Y' ) , SJD AS --수주상세 ( SELECT M.CO_CD AS CO_CD --회사코드 , (SELECT FN_CM05M01_CD_TO_NM(M.CO_CD) FROM DUAL) AS CO_NM --회사명 , M.ORDRS_NO AS ORDRS_NO --수주번호 , X.SALES_CD AS SALES_CD --SALES Code , M.ORDRS_CLNT_CD AS CLNT_CD --고객코드 , C.CLNT_NM AS CLNT_NM --고객명 , M.CLNT_PJT AS CLNT_PJT --고객사프로젝트 , (SELECT FN_CM05M01_CD_TO_NM(M.CLNT_PJT) FROM DUAL) AS CLNT_PJT_NM --고객사프로젝트명 , X.ORDRS_PRC_MACH AS ORDRS_PRC_MACH --수주가-기계파트 , X.TRGT_PCHS_PCOST_MACH AS TRGT_PCOST_MACH--목표구매원가-기계파트 FROM TB_CR02M01 M --수주마스터 INNER JOIN TB_CR02D02 AS X ON X.CO_CD = M.CO_CD AND X.ORDRS_NO = M.ORDRS_NO--수주상세 INNER JOIN TB_BM02M01 AS C ON M.ORDRS_CLNT_CD = C.CLNT_CD --거래처정보 WHERE 1=1 AND M.CO_CD = #{coCd} AND X.SALES_CD IS NOT NULL --비용제외 AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = X.CO_CD AND P.SALES_CD = X.SALES_CD ) ) , EVAL AS --WBS 평가정보 ( SELECT W.CO_CD AS CO_CD --회사코드 , W.SALES_CD AS SALES_CD --SALSES_CD , TO_CHAR(W.EVL_DT, 'YYYY-MM-DD') AS EVL_DT --평가일자 , W.EVL_ID AS EVL_ID --평가자ID , (SELECT FN_CM06M01_ID_TO_NM(W.EVL_ID) FROM DUAL) AS EVL_NM --평가자명 , W.CLOSE_YN AS CLOSE_YN --확정여부 , W.EVL_CNTS AS EVL_CNTS --평가내용 , W.FILE_TRGT_KEY AS FILE_TRGT_KEY FROM TB_WB25M01 W--WBS평가 WHERE W.CO_CD = #{coCd} AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = W.CO_CD AND P.SALES_CD = W.SALES_CD ) ) , RSLT AS --WBS 실적정보 ( SELECT WR.CO_CD AS CO_CD--회사코드 , WR.SALES_CD AS SALES_CD--SALES Code , MIN(WR.WBS_RSLTSS_DT) AS RSLTSS_DT --TASK실적시작일 , MAX(WR.WBS_RSLTSE_DT) AS RSLTSE_DT --TASK실적종료일 , SUM(WR.DAYCNT) AS RSLT_DAYCNT --TASK실적소요일 , SUM(WR.WBS_RSLTS_MH) AS RSLTS_MH --WBS실적 공수 , MAX(CASE WHEN WR.WBS_PLAN_CODE_KIND = 'WBSCODE15' AND WR.WBS_RSLTS_RATE = 100 THEN 'Y' ELSE 'N' END) AS WBS_CLOSE_YN --평가대상 유무 : 완료검수 실적(WBSCODE15) = 100% FROM TB_WB23M01 WR--WBS실적 정보 WHERE WR.CO_CD = #{coCd} AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = WR.CO_CD AND P.SALES_CD = WR.SALES_CD ) GROUP BY WR.CO_CD, WR.SALES_CD ) , ISU AS --WBS 이슈정보 ( SELECT ISU.CO_CD --회사코드 , ISU.SALES_CD --SALES Code , SUM(CASE WHEN ISU.WBS_PLAN_NO IS NOT NULL THEN 1 ELSE 0 END ) AS PLAN_CNT --계획 이슈 , SUM(CASE WHEN ISU.WBS_RSLTS_NO IS NOT NULL THEN 1 ELSE 0 END ) AS RSLT_CNT --실적 이슈 FROM TB_WB24M02 AS ISU --WBS이슈 정보 WHERE CO_CD = #{coCd} AND EXISTS (SELECT 'X' --조회된 과제의 SALES_CD 만 FROM SUBJ P WHERE P.CO_CD = #{coCd} AND P.CO_CD = ISU.CO_CD AND P.SALES_CD = ISU.SALES_CD ) GROUP BY CO_CD, SALES_CD )
SELECT * FROM ( SELECT ROWNUM AS RN, T.* FROM ( SELECT X.CO_CD AS CO_CD --회사코드 , CR.ORDRS_NO AS ORDRS_NO --수주번호 , CR.CLNT_CD AS CLNT_CD --고객코드 , CR.CLNT_NM AS CLNT_NM --고객명 , CR.CLNT_PJT AS CLNT_PJT --고객사프로젝트 , CR.CLNT_PJT_NM AS CLNT_PJT_NM --고객사프로젝트명 , X.SJ_NM AS SJ_NM --과제명 , X.VER_NO AS VER_NO --과제버젼 , X.SALES_CD AS SALES_CD --SALES Code , X.SMRIZE_NM AS SMRIZE_NM --총괄PM명 , X.BEGIN_DT AS BEGIN_DT --과제시작일 , X.DE_DT AS DE_DT --출고일 , X.ACPTNC_DT AS ACPTNC_DT --완료검수일 , X.REQRE_DAYCNT AS REQRE_DAYCNT --소요일수 , TO_CHAR(RS.RSLTSS_DT, 'YYYY-MM-DD') AS RSLTSS_DT --TASK실적시작일 , TO_CHAR(RS.RSLTSE_DT, 'YYYY-MM-DD') AS RSLTSE_DT --TASK실적종료일 , RS.RSLT_DAYCNT AS RSLT_DAYCNT --TASK실적소요일 , RS.RSLTS_MH AS RSLTS_MH --TASK실적 공수 , ISU.PLAN_CNT AS PLAN_CNT --계획 이슈 , ISU.RSLT_CNT AS RSLT_CNT --실적 이슈 , NVL(RS.WBS_CLOSE_YN,'N') AS WBS_CLOSE_YN --평가대상 , EV.EVL_DT AS EVL_DT --평가일자 , EV.EVL_ID AS EVL_ID --평가자ID , EV.EVL_NM AS EVL_NM --평가자명 , NVL(EV.CLOSE_YN,'N') AS PLAN_CLOSE_YN --계획확정여부 , EV.EVL_CNTS AS EVL_CNTS --평가내용 , EV.FILE_TRGT_KEY AS FILE_TRGT_KEY --FILE_TRGT_KEY FROM SUBJ X INNER JOIN SJD AS CR ON X.CO_CD = CR.CO_CD AND X.SALES_CD = CR.SALES_CD LEFT OUTER JOIN EVAL AS EV ON X.CO_CD = EV.CO_CD AND X.SALES_CD = EV.SALES_CD LEFT OUTER JOIN RSLT AS RS ON X.CO_CD = RS.CO_CD AND X.SALES_CD = RS.SALES_CD LEFT OUTER JOIN ISU AS ISU ON X.CO_CD = ISU.CO_CD AND X.SALES_CD = ISU.SALES_CD WHERE 1=1 ) T WHERE 1=1 AND T.CO_CD = #{coCd}
AND T.SALES_CD = #{salesCd}
AND T.CLOSE_YN = #{closeYn}
AND T.WBS_CLOSE_YN = #{wbsCloseYn}
AND T.CLNT_PJT = #{clntPjt}
AND T.CLNT_NM LIKE '%' || #{ordrsClntNm} || '%'
AND T.BEGIN_DT BETWEEN #{beginDt} AND #{deDt} ORDER BY CLNT_CD, ORDRS_NO) W WHERE RN BETWEEN #{firstIndex} AND #{lastIndex}
